mapper.sql:
(1!=1

    or account = #{account}
    or phone = #{account}
    or email = #{account}

)

mapper.select:
select r.id, r.role_name, r.role_code, 0 source from sso_user u inner join sso_client_user cu on cu.user_id = u.id inner join sso_user_role ur on ur.user_id = u.id inner join sso_role r on r.id = ur.role_id
r.status = 0 and r.del_flag = 0 and u.id = #{userId}
and r.client_id = #{clientId}
union select r.id, r.role_name, r.role_code,1 source from sso_user u inner join sso_client_user cu on cu.user_id = u.id inner join sso_org_user ou on u.id = ou.user_id inner join sso_org_role sor on ou.org_id = sor.org_id INNER JOIN sso_role r on r.id = sor.role_id
r.status = 0 and r.del_flag = 0 and u.id = #{userId}
and r.client_id = #{clientId}

select GROUP_CONCAT(u.permissions) permissions from ( select u.id,m.permissions from sso_user u inner join sso_client_user cu on cu.user_id = u.id inner join sso_user_role ur on ur.user_id = u.id inner join sso_role r on r.id = ur.role_id inner join sso_role_menu rm on rm.role_id = ur.role_id inner join sso_menu m on rm.menu_id = m.id
u.id = #{userId} and m.permissions is not null and m.permissions != ''
and cu.client_id = #{clientId}
union select u.id,m.permissions from sso_user u inner join sso_client_user cu on cu.user_id = u.id inner join sso_org_user ou on u.id = ou.user_id inner join sso_org_role sor on ou.org_id = sor.org_id inner join sso_role_menu rm on rm.role_id = sor.role_id inner join sso_menu m on rm.menu_id = m.id
u.id = #{userId} and m.permissions is not null and m.permissions != ''
and cu.client_id = #{clientId}
) u group by u.id order by u.permissions



    u.del_flag = 0

        and u.org_id = #{orgId}


        and u.phone like concat('%',#{phone},'%')


        and u.account like concat('%',#{account},'%')


        and u.nickname like concat('%',#{nickname},'%')


        and u.status = #{status}


        and u.client_id = #{clientId}


group by u.id
order by u.create_time desc

mapper.delete:
delete from sso_org_user

    user_id = #{userId}

        and org_id = #{orgId}
