mapper.sql:
(1!=1

    or account = #{account}
    or phone = #{account}
    or email = #{account}

)

mapper.select:
select r.id, r.role_name, r.role_code, 0 source from sso_user u inner join sso_user_role ur on ur.user_id = u.id inner join sso_role r on r.id = ur.role_id
u.id = #{userId}
union select r.id, r.role_name, r.role_code,1 source from sso_user u inner join sso_org_user ou on u.id = ou.user_id inner join sso_org o on o.id = ou.org_id
inner join sso_org_role sor on ou.org_id = sor.org_id INNER JOIN sso_role r on r.id = sor.role_id and r.status = 0 and r.del_flag = 0
u.id = #{userId}

sql.where:
u.id = #{userId}
and m.permissions is not null
and m.permissions != ''

mapper.insert:
INSERT into sso_user_role (select #{userId} user_id, id role_id
from sso_role
where role_code in

    #{roleCode}

)

mapper.delete:
delete from sso_org_user

    user_id = #{userId}

        and org_id in

            #{orgId}

mapper.select:
select count(0)
from sso_org_user
where user_Id = #{userId}
  and org_id = #{orgId}
